// src/components/Registration.js
import React, { useState } from 'react';
import './Registration.css';

const Registration = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation
    if (!username || !password) {
      setErrorMessage('Please fill in all fields.');
      return;
    }

    // Clear error message on successful submission
    setErrorMessage('');

    // Prepare user data for submission
    const userData = {
      username,
      password,
    };

    // Simulate a POST request (replace with actual API call)
    console.log('Sending data:', userData);
    
    // Here you would typically use fetch or axios to send data to your backend.
    alert('Registration successful!'); // For demonstration
  };

  return (
    <div className="container">
      <h3 style={{ color: 'red', display: errorMessage ? 'block' : 'none' }}>{errorMessage}</h3>
      <h2>User Registration</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <input type="submit" value="Register" />
      </form>
      <p>
        Already have an account? <a href="/login">Login here</a>
      </p>
    </div>
  );
};

export default Registration;
