// src/components/Navbar.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = ({ userRole }) => {
    const navigate = useNavigate();
    
    const handleLogout = (e) => {
        e.preventDefault();
        alert("You have logged out");
        navigate("/login");
        // Add your logout logic here (e.g., clearing user data, redirecting, etc.)
    };

    const handleRedirect = (role) => {
        if (role.equals("ROLE_USER")) {
            return "/userTournaments";
        } else if (role.equals("ROLE_ADMIN")) {
            return "/adminTournaments";
        }
    }

    return (
        <nav className="navbar">
            <div className="nav-container">
            <form onSubmit={handleLogout} className="logout-form">
                    <button type="submit" className="logout-button">Log Out</button>
                </form>
                
                {userRole === 'ROLE_USER' && (
                    <><ul className="navbar-nav">
                        <li className="nav-item"><Link className="nav-link" to={handleRedirect()}>Tournaments</Link></li>
                        <li className="nav-item"><Link className="nav-link" to="/profile">Profile</Link></li>
                    </ul></>
                )};

                {userRole === 'ROLE_ADMIN' && (
                    <><ul className="navbar-nav">
                        <li className="nav-item"><Link className="nav-link" to={handleRedirect()}>Tournaments</Link></li>
                    </ul></>
                )};
                
                <form onSubmit={handleLogout} className="logout-form">
                    <button type="submit" className="logout-button">Log Out</button>
                </form>
            </div>
        </nav>
    );
};

export default Navbar;
