// src/components/Registration.js
import React, { useState } from 'react';
import './Registration.css'

const Registration = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setErrorMessage('Please fill in all fields.');
      return;
    }

    setErrorMessage('');

    const userData = {
      username:username,
      password:password,
      authorities:'ROLE_USER',
    };

    try {
      const response = await fetch('http://localhost:8080/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      const responseText = await response.text(); // Get the raw text response
console.log('Response:', responseText); // Log the raw response

if (!response.ok) {
    throw new Error(responseText); // Use the raw response as the error message
}

const data = await response.json(); // Parse the JSON response

      
      alert(data); // Handle success response
    } catch (error) {
      setErrorMessage(error.message);
      console.error('Error:', error);
    }
  };

  return (
    <div className="container">
      <h3 style={{ color: 'red', display: errorMessage ? 'block' : 'none' }}>{errorMessage}</h3>
      <h2>User Registration</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <input type="submit" value="Register" />
      </form>
      <p>
        Already have an account? <a href="/login">Login here</a>
      </p>
    </div>
  );
};

export default Registration;
