// src/components/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ userRole }) => {
    return (
        <nav className="navbar">
            <div className="container">
                <Link className="navbar-brand" to="/">Chess Tournament Manager</Link>
                <ul className="navbar-nav">
                    {userRole === 'ROLE_USER' && (
                        <>
                            <li className="nav-item"><Link className="nav-link" to="/tournaments">Tournaments</Link></li>
                            <li className="nav-item"><Link className="nav-link" to="/profile">Profile</Link></li>
                        </>
                    )}
                    {userRole === 'ROLE_ADMIN' && (
                        <li className="nav-item"><Link className="nav-link" to="/admin/tournaments">Admin Tournaments</Link></li>
                    )}
                </ul>
                <form action="/logout" method="post" className="logout-form">
                    <button type="submit" className="logout-button">Log Out</button>
                </form>
            </div>
        </nav>
    );
};

export default Navbar;
